"use strict";

function _toConsumableArray(a) {
    if (Array.isArray(a)) {
        for (var b = 0, c = Array(a.length); b < a.length; b++) {
            c[b] = a[b];
        }
        return c;
    }
    return Array.from(a);
}

var playing = true;

var timer = function() {
    return setInterval(function() {
        var counter = document.getElementById("counter");
        var currentCount = parseInt(counter.innerText);
        counter.innerText = currentCount + 1;
    }, 1000);
};

var interval = timer();
var minus = document.getElementById("minus");
var plus = document.getElementById("plus");
var heart = document.getElementById("heart");
var pause = document.getElementById("pause");
var commentForm = document.getElementsByTagName("form")[0];

minus.addEventListener("click", function() {
    var counter = document.getElementById("counter");
    var currentCount = parseInt(counter.innerText);
    counter.innerText = currentCount - 1;
});

plus.addEventListener("click", function() {
    var counter = document.getElementById("counter");
    var currentCount = parseInt(counter.innerText);
    counter.innerText = currentCount + 1;
});

heart.addEventListener("click", function() {
    var counter = document.getElementById("counter");
    var currentCount = parseInt(counter.innerText);
    var likesList = document.querySelector(".likes");
    var existingLike = [].concat(_toConsumableArray(likesList.children)).find(function(like) {
        return parseInt(like.dataset.num) === currentCount;
    });

    if (existingLike) {
        var likeCount = parseInt(existingLike.children[0].innerText);
        existingLike.innerHTML = currentCount + " has been liked <span>" + (likeCount + 1) + "</span> times";
    } else {
        var newLike = document.createElement("li");
        newLike.setAttribute("data-num", currentCount);
        newLike.innerHTML = currentCount + " has been liked <span>1</span> time";
        likesList.appendChild(newLike);
    }
});

pause.addEventListener("click", function() {
    if (playing) {
        playing = false;
        clearInterval(interval);
        this.innerText = "resume";
    } else {
        playing = true;
        interval = timer();
        this.innerText = "pause";
    }

    [].concat(_toConsumableArray(document.getElementsByTagName("button"))).forEach(function(a) {
        if (a.id !== "pause") {
            a.disabled = !playing;
        }
    });
});

commentForm.addEventListener("submit", function(e) {
    e.preventDefault();
    var commentInput = this.children[0];
    var commentText = commentInput.value;
    commentInput.value = "";
    var commentsList = document.querySelector(".comments");
    var newComment = document.createElement("p");
    newComment.innerText = commentText;
    commentsList.appendChild(newComment);
});